import axios from 'axios';
import {Redirect} from 'react-router-dom';
const fire = require('../images/fire.png');
const logo = require('../images/propit.png');



class Login extends Component {

    details = {
        username: '',
        password: ''
      }

    constructor(props){
        super(props);
    }
    callApi(){
        console.log(this.details)
        const config = {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
            },
            data: {},
            params: this.details
            
        }

        axios.post('http://localhost:3001/login', {
            username: this.details.username,
            password: this.details.password
          })
          .then(function (response) {
            console.log(response);
            localStorage.setItem('tokenP',response.data)
          })
          .catch(function (error) {
            console.log(error);
          });
      
    }

    handleNameChange(event){
        this.details.username = event.target.value;
        console.log(this.details.username)
    }
    handlePasswordChange(event){
        this.details.password = event.target.value;
        console.log(this.details.password)
    }

    render() {
      return (<div>
        <div>א<input onChange={e => {this.handleNameChange(e)}}/></div>
        <div>ס<input onChange={e => {this.handlePasswordChange(e)}}/></div>
        <div><button onClick={() => this.callApi()}>לחץ</button></div>
    </div>);
    }
    


}

export default Login;
